mapper.select:
SELECT NVL((SELECT ID 
			  FROM BOOK 
			 WHERE 1=1


			   AND ISBN10 = #{ISBN10} 


			   AND ISBN13 = #{ISBN13}


			 	AND 1 != 1


			),(SELECT CONCAT('B', LPAD(COALESCE(SUBSTR(MAX(ID), 2), 0) + 1, 10, '0')) FROM BOOK)) AS ID

SELECT
	SEQ 
FROM AUTHOR
WHERE BOOK_ID = #{book_id}
AND NAME = #{name}

mapper.update:
  	UPDATE AUTHOR SET
		TYPE = #{type},
		NAME = #{name},
		MOD_DATETIME = SYSDATE()
  	WHERE BOOK_ID = #{book_id}
  	AND SEQ = #{seq}
